value.value:
@toLower(
    concat(
        variables('v_diretorio_destino'),
        '/', 
        pipeline().parameters.p_assunto_origem,
        if(
            empty(variables('v_data_particao')),
            '',
            concat('/', variables('v_data_particao'))
        )
    )
)

@toLower(
    concat(
        pipeline().parameters.p_assunto_origem,
        '__', 
        if(
            empty(variables('v_data_particao')),
            formatDateTime(utcNow(), 'yyyyMMdd'),
            replace(variables('v_data_particao'), '/', '')
        )
    )
)

@toLower(
    concat(
        pipeline().parameters.p_diretorio_destino,
        variables('v_diretorio_destino'),
        '/', 
        pipeline().parameters.p_assunto_origem,
        if(
            empty(variables('v_data_particao')),
            '',
            concat('/', variables('v_data_particao'))
        )
    )
)

@string(
    div(
        div(
            div(
                div(
                    sub(
                        ticks(
                            formatDateTime(utcNow(), 'yyyy/MM/dd HH:mm:ss')
                        ),
                        ticks(
                            if(
                                empty(pipeline().parameters.p_data_inicio_carga), 
                                formatDateTime(addDays(utcNow(),-90), 'yyyy/MM/dd HH:mm:ss'),
                                formatDateTime(pipeline().parameters.p_data_inicio_carga, 'yyyy/MM/dd HH:mm:ss') 
                            )   
                        )
                    ),
                    10000000
                ),
                60
            ),
            60
        ),
        24
    )
)

@toLower(
    concat(
        variables('v_diretorio_destino'),
        '/', 
        pipeline().parameters.p_colecao_origem,
        if(
            empty(variables('v_data_particao')),
            '',
            concat('/', variables('v_data_particao'))
        )
    )
)

@toLower(
    concat(
        pipeline().parameters.p_colecao_origem,
        '__', 
        if(
            empty(variables('v_data_particao')),
            formatDateTime(utcNow(), 'yyyyMMdd'),
            replace(variables('v_data_particao'), '/', '')
        )
    )
)

@toLower(concat(
    variables('v_diretorio_destino'),
    '/', 
    pipeline().parameters.p_tabela_origem,
    if(
        empty(variables('v_data_particao')),
        '',
        concat('/', variables('v_data_particao'))
    )
))

@if(
    empty(pipeline().parameters.p_select_origem), 
    concat(
        'SELECT * FROM ', 
        pipeline().parameters.p_esquema_origem, '.',
        pipeline().parameters.p_tabela_origem, ' WHERE 1=1 '
    ),
    pipeline().parameters.p_select_origem
)

@if(
    greater(activity('CPD - Ingere Tabela na Raw').output.rowsCopied, 0),
    true,
    false
)

notebookPath.value:
@concat(
    if(
        endswith(
            trim(pipeline().parameters.p_caminho_raiz_notebook),
            '/'
        ),
        trim(pipeline().parameters.p_caminho_raiz_notebook),
        concat(trim(pipeline().parameters.p_caminho_raiz_notebook),'/')
    ),
    if(
        startswith(
            trim(pipeline().parameters.p_caminho_notebook),
            '/'
        ),
        substring(
            trim(pipeline().parameters.p_caminho_notebook),
            1,
            sub(length(pipeline().parameters.p_caminho_notebook),1)
        ),
        trim(pipeline().parameters.p_caminho_notebook)
    ),
    if(
        endswith(
            trim(pipeline().parameters.p_caminho_notebook), 
            '/'
        ),
        if(
            startswith(
                trim(pipeline().parameters.p_nome_notebook), 
                '/'
            ),
            substring(
                trim(pipeline().parameters.p_nome_notebook),
                1,
                sub(length(pipeline().parameters.p_nome_notebook),1)
            ),
            trim(pipeline().parameters.p_nome_notebook)
        ), 
        if(
            startswith(
                trim(pipeline().parameters.p_nome_notebook), 
                '/'
            ),
            trim(pipeline().parameters.p_nome_notebook),
            concat('/', trim(pipeline().parameters.p_nome_notebook))
        )
    )
)
